try:
    import gmpy2
    from Crypto.Util.number import GCD

    def neg_pow(a, b, n):
        assert b < 0
        assert GCD(a, n) == 1
        res = int(gmpy2.invert(a, n))
        res = pow(res, b*(-1), n)
        return res

    def common_modulus(e1, e2, n, c1, c2):
    	g, a, b = egcd(e1, e2)
    	if a < 0:
    		c1 = neg_pow(c1, a, n)
    	else:
    		c1 = pow(c1, a, n)
    	if b < 0:
    		c2 = neg_pow(c2, b, n)
    	else:
    		c2 = pow(c2, b, n)
    	ct = c1*c2 % n
    	m = int(gmpy2.iroot(ct, g)[0])
    	return m
    
    c1 = int(input(">>> c1 = "))
    c2 = int(input(">>> c2 = "))
    e1 = int(input(">>> e1 = "))
    e2 = int(input(">>> e2 = "))
    n = int(input(">>> n = "))
  
    Convert(common_modulus(e1, e2, n, c1, c2))
except ImportError:
    slowprint("\n[-] Module Not Setup")
except ValueError:
    slowprint("\n[-] e1, e2, n, c1, c2 Must Be Integar Number")
except AssertionError:
    slowprint("\n[-] Wrong Data")
